<?php if($_SERVER["REQUEST_METHOD"]==="POST"){function limpiar($dato){return htmlspecialchars(strip_tags(trim($dato)));}$nombre=limpiar($_POST["nombre"]?? "");$correo=limpiar($_POST["correo"]?? "");$mensaje=limpiar($_POST["mensaje"]?? "");if(empty($nombre)||empty($correo)||empty($mensaje)||!filter_var($correo,FILTER_VALIDATE_EMAIL)){echo "<h2 style='color: red; text-align:center;'>❌ Error: Datos inválidos o incompletos.</h2>";exit;}echo"<h2 style='color: green; text-align:center;'>✅ Gracias, $nombre. Tu mensaje se ha enviado correctamente.</h2>";echo "<p style='text-align:center;'><a href='formulario.html'>Volver</a></p>";}else{header("Location: formulario.html");exit;}
?>
